package PracticeOne;

public class SwipeOfNumberWithoutUsinThirdVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10,y=20;
		System.out.println("After swipe "+x  +"  "+y );

		x=x+y;//30
		y=x-y;//10
		x=x-y;//20
		System.out.println("After swipe "+x  +"  "+y );

	}

}
